Ext.define('MyApp.controller.mcButton', {
    extend: 'Ext.app.Controller',

    control: {
        "button": {
            click: 'onButtonClick'
        }
    },

    onButtonClick: function(button, e, eOpts) {
        var id=button.id;
        switch(id) {
            case 'btn_Mantenedor_cargo_refresh':
                Ext.getStore("js_Cargos").load();
                Ext.getCmp("txt_Mantenedor_cargo_buscar").reset();
                Ext.getCmp("frm_Mantenedorcargo").reset();
                break;
            case 'btn_Mantenedor_cargo_buscar':
                var filtro = Ext.getCmp("txt_Mantenedor_cargo_buscar").getValue();
                Ext.getStore("js_Cargos").load({params : {"filtro": filtro} });
                break;
            case 'btn_Mantenedor_cargo_nuevo':
                Ext.getCmp("frm_Mantenedorcargo").reset();
                Ext.getCmp("frm_Mantenedorcargo").getForm().findField('cargo').setReadOnly(false);
                Ext.getCmp('hidden_cargo_tipo').setValue(0);
                break;
            case 'btn_Mantenedor_cargo_grabar':
                this.grabarCargo();
                break;
        }
    },

    grabarCargo: function() {
        var error=false;
        var form = Ext.getCmp("frm_Mantenedorcargo").getForm();
        var tipo = Ext.getCmp("hidden_cargo_tipo").getValue();

        form.submit({
            url:"../../../servlet/EjeCoreI?claseweb=cl.eje.view.sencha.Conf&modulo=eje_generico_mantenedorcargos&accion=save&thing=Cargo",
            method: "POST",
            params:	{ "tipo" : tipo },
            waitMsg	: 'Grabando los datos del cargo...',
            success	: function(form,result){
                try {
                    var data = Ext.JSON.decode(result.response.responseText);
                    if (data.success==="true" || data.success===true){
                        Ext.Msg.show({
                            title: 'Actualizaci√≥n',
                            msg: 'Los datos del Cargo fueron guardados correctamente.',
                            buttons: Ext.Msg.OK,
                            icon: Ext.Msg.INFO
                        });
                        Ext.getCmp("frm_Mantenedorcargo").reset();
                        Ext.getStore("js_Cargos").load();
                    }
                    else{
                        error=true;
                    }
                }
                catch(e){
                    error=true;
                }
                if (error===true){
                    var mensg='<p>Ha ocurido un error al tratar de grabar la informaci\u00f3n.</p> ';
                    mensg=mensg+'<p>Por favor int\u00e9ntelo nuevamente.</p> ';
                    Ext.Msg.show({
                        title: 'Error inesperado',
                        msg: mensg,
                        buttons: Ext.Msg.OK,
                        icon: Ext.Msg.ERROR
                    });
                }
            },
            complete: function() {

            }
        });
    }

});
