Ext.define('MyApp.controller.mcGrid', {
    extend: 'Ext.app.Controller',

    control: {
        "gridpanel": {
            itemclick: 'onGridpanelItemClick'
        }
    },

    onGridpanelItemClick: function(dataview, record, item, index, e, eOpts) {
        var id = dataview.id;
        switch(id) {
            case 'grid_Mantenedor_cargo_lista_view' :
                var cargo = record.get("cargo");
                Ext.getCmp('hidden_cargo_tipo').setValue(1);
                Ext.getCmp("frm_Mantenedorcargo").getForm().findField('cargo').setReadOnly(true);
                Ext.getStore("js_FichaCargo").load({params : { "cargo" : cargo } });
                break;
        }
    }

});
