Ext.define('MyApp.controller.mcInput', {
    extend: 'Ext.app.Controller',

    control: {
        "textfield": {
            change: 'onTextfieldChange'
        }
    },

    onTextfieldChange: function(field, newValue, oldValue, eOpts) {
        var id = field.id;
        switch(id) {
            case "txt_Codigo_cargo":
                var codigo = Ext.getCmp("txt_Codigo_cargo").getValue();
                MyApp.app.getController('mcInput').noexisteCargo(codigo);
                break;
        }
    },

    noexisteCargo: function(codigo) {
        var error=false;
        var form = Ext.getCmp("frm_Mantenedor_cargo_existencia").getForm();
        form.submit({
            url:"../../../servlet/EjeCoreI?claseweb=cl.eje.view.sencha.Conf&modulo=eje_generico_mantenedorcargos&accion=exists&thing=Cargo",
            method: "POST",
            params:	{ "cargo" : codigo },
            //waitMsg	: 'Validando Codigo del Cargo...',
            success	: function(form,result){
                try {
                    var data = Ext.JSON.decode(result.response.responseText);
                    if (data.success==="true" || data.success===true){
                        if (data.data[0].msg==="true") {
                            Ext.getCmp("img_Cargo").setSrc('../../images/ok.png');
                            Ext.getCmp("txt_Nombre_cargo").setDisabled(false);
                            Ext.getCmp("chb_Vigente_cargo").setDisabled(false);
                            Ext.getCmp("btn_Mantenedor_cargo_grabar").setDisabled(false);
                        }
                        else {
                            var tipo = Ext.getCmp('hidden_cargo_tipo').getValue();
                            if(tipo === 0 || tipo === "0" ) {
                                Ext.getCmp("img_Cargo").setSrc('../../images/nook.png');
                                Ext.getCmp("txt_Nombre_cargo").setDisabled(true);
                                Ext.getCmp("chb_Vigente_cargo").setDisabled(true);
                                Ext.getCmp("btn_Mantenedor_cargo_grabar").setDisabled(true);
                            }
                            else {
                                Ext.getCmp("img_Cargo").setSrc('../../images/ok.png');
                                Ext.getCmp("txt_Nombre_cargo").setDisabled(false);
                                Ext.getCmp("chb_Vigente_cargo").setDisabled(false);
                                Ext.getCmp("btn_Mantenedor_cargo_grabar").setDisabled(false);
                            }
                        }
                    }
                }
                catch(e){

                }
            },
            complete: function() {

            }
        });
    }

});
