Ext.define('MyApp.controller.gridController', {
    extend: 'Ext.app.Controller',

    control: {
        "gridpanel": {
            itemclick: 'onGridpanelItemClick'
        }
    },

    onGridpanelItemClick: function(dataview, record, item, index, e, eOpts) {
        var id = dataview.id;
        switch(id) {
            case 'grid_Mantenedor_colaborador_lista_view' :
                var rut = record.get("rut");
                Ext.getCmp('hidden_colaborador_tipo').setValue(1);
                Ext.getCmp("frm_Mantenedorcolaborador").getForm().findField('rut').setReadOnly(true);
                Ext.getCmp("frm_Mantenedorcolaborador").getForm().findField('digito_ver').setReadOnly(true);
                Ext.getStore("js_FichaColaborador").load({params : { "rut" : rut } });
                break;
        }
    }

});
