Ext.define('MyApp.controller.detalle', {
    extend: 'Ext.app.Controller',

    delRegDetalle: function(view, rowIndex, colIndex, item, e, record, row) {
        if(  MyApp.app.getController("main").isInvitado() == true ) {
            Ext.MessageBox.alert("Error", "No tienes permiso para ejecutar esta acci&oacute;n");
        }
        else {
            Ext.MessageBox.confirm('Eliminar', '¿Estás seguro que desea eliminar el registro?', function(btn){
               if(btn === 'yes'){
                   var data = record.data;
                    data['claseweb'] = "cl.imasd.view.sencha.anywhere.Conf";
                    data['modulo']='anywhere_ipos_administraralertas';
                    data['thing']='DetalleRegistros';
                    data['accion']='del';

                    Ext.Ajax.request({
                        url: '../../../EjeCoreI',
                        params: data,
                        success: function(response){
                            var text = JSON.parse(response.responseText);

                            if(text.success == true) {
                               Ext.getStore("jsGetDetallenEW").remove(record);
                            }
                            else {
                               Ext.MessageBox.alert('Error', 'No fue posible eliminar el registro');
                            }
                        },
                        error: function() {

                        }
                    });
               }

             });
        }


    },

    updRegDetalle: function(editor, context,eOpts) {


        console.log(editor);
        console.log(context);
        console.log(eOpts);

        var data = context.record.data;
        data['claseweb'] = "cl.imasd.view.sencha.anywhere.Conf";
        data['modulo']='anywhere_ipos_administraralertas';
        data['thing']='DetalleRegistros';
        data['accion']='upd';
        data['newregistro']= context.newValues.registro;

        Ext.Ajax.request({
            url: '../../../EjeCoreI',
            params: data,
            success: function(response){
                var text = JSON.parse(response.responseText);

                if(text.success != true) {
                    Ext.MessageBox.alert('Error', 'No fue posible modificar el registro');
                }
            },
            error: function() {

            }
        });
    }

});
